mapper.update:

UPDATE FREE_T
   SET GROUP_ORDER = GROUP_ORDER + 1
 WHERE GROUP_NO = #{groupNo}     
   AND GROUP_ORDER > #{groupOrder}

mapper.insert:

INSERT INTO FREE_T (
    FREE_NO
  , EMAIL
  , CONTENTS
  , CREATED_AT
  , STATUS
  , DEPTH
  , GROUP_NO
  , GROUP_ORDER
) VALUES (
    FREE_SEQ.NEXTVAL
  , #{email}
  , #{contents}
  , SYSTIMESTAMP
  , 1
  , #{depth}   
  , #{groupNo}
  , #{groupOrder}
)

mapper.update:
UPDATE FREE_T
   SET STATUS = 0
 WHERE FREE_NO = #{freeNo}
